import React, {useState} from 'react';
import {
    Flex,
    Box,
    Heading,
    FormControl,
    FormLabel,
    Input,
    Button,
    CircularProgress
} from '@chakra-ui/react';
import {userLogin} from '../Components/loginApi';
import ErrorMessage from '../Components/LoginError'; 

export default function LoginPage() {


        const [email, setEmail] = useState('');
        const [password, setPassword] = useState('');
        const [error, setError] = useState('');
        const [isLoading, setIsLoading] = useState(false);
        const handleSubmit = async event => {
          event.preventDefault();
          setIsLoading(true);
          try {
            await userLogin({ email, password });
            setIsLoading(false);
          } catch (error) {
            setError('Invalid username or password');
            setIsLoading(false);
            setEmail('');
            setPassword('');
          }
        };





    return (
        <Flex width="full" align="center" justifyContent="center">
            <Box p={8}
                maxWidth="500px"
                borderWidth={1}
                borderRadius={8}
                boxShadow="lg">
                <Box textAlign="center">
                    <Heading>Login</Heading>
                </Box>
                <Box my={4}
                    textAlign="left">
                    <form onSubmit={handleSubmit}>
                    {error && <ErrorMessage message={error} />}
                        <FormControl isRequired>
                            <FormLabel>Email</FormLabel>
                            <Input type="email" placeholder="test@test.com" size="lg"
                                onChange={
                                    event => setEmail(event.currentTarget.value)
                                }/>
                        </FormControl>
                        <FormControl isRequired
                            mt={6}>
                            <FormLabel>Password</FormLabel>
                            <Input type="password" placeholder="*******" size="lg"
                                onChange={
                                    event => setPassword(event.currentTarget.value)
                                }/>
                        </FormControl>
                        <Button variantColor="teal" variant="outline" type="submit" width="full"
                            mt={4}>
                            {isLoading ? (
    <CircularProgress isIndeterminate size="24px" color="teal" />
  ) : (
    'Sign In'
  )}
                        </Button>
                    </form>

                </Box>
            </Box>
        </Flex>
    );
}
