import React from 'react';
import {Grid, GridItem, Input, Stack} from "@chakra-ui/react"
import {Button, ButtonGroup} from "@chakra-ui/react"
import TrendingMovies from '../Components/TrendingMovie'; 


export default function Homepage() {



    return (
        <div>

            <Grid h="900px" templateRows="repeat(3, 1fr)" templateColumns="repeat(1, 1fr)"
                gap={4}
                backgroundColor="primary.100">
                {/* 1st section of page, this holds the search bar and the top menu buttons  */}
                <GridItem colSpan={6}>

                    <Stack direction="row"
                        spacing={5}
                        align="center">
                        <Button colorScheme="orange" variant="solid">
                            Movie
                        </Button>
                        <Button colorScheme="orange" variant="outline">
                            Actor
                        </Button>
                        <Button colorScheme="orange" variant="outline">
                            Genre
                        </Button>
                        <Button colorScheme="orange" variant="outline">
                            Advance Search
                        </Button>
                        <Button colorScheme="yellow" variant="outline">
                            My Hub
                        </Button>

                    </Stack>
                    <Input style={
                            {
                                marginTop: "10px",
                                width: "90%"
                            }
                        }
                        placeholder="Search"
                        size="lg"
                        bg="white"/>
                </GridItem>

                <GridItem colSpan={6}> 
             <TrendingMovies></TrendingMovies>

                </GridItem>
                <GridItem colSpan={6}
                    bg="primary.500"/>

            </Grid>
        </div>
    );
}
