import React, { Component } from 'react';
import {Grid, GridItem,Box, Text, Center, Wrap} from "@chakra-ui/react";

class HighestRatedMovies extends React.Component{
    constructor(props) {
        super(props);
        this.state = { 
          highestrated:[] ,
          titleId:''
        };
      }


      getHighestRated() {
        fetch('http://18.206.168.148:5000/movies/highestrated ')
          .then(response => response.json())
          .then(response => {
            this.setState({
              highestrated: response
            })
            console.log(this.state)
          })
      }


      componentDidMount() {
        this.getHighestRated(); 
      }

      handleClick(movie) {
        //e.preventDefault();
       window.location.href = "/#/movie/"+ movie.titleId;
        console.log(movie); 
      }

      render(){
          return (
