    //       <h1 >{movie.primaryTitle}</h1>
    //       <p>Rated {movie.avgRating} by {movie.numOfVotes} viewers.</p>
    //     </div>
    //     </Box>  
    // ))}

    //     </Grid>
    //     </div>
    //       );
    //   }


    <div>
    <Center> 

                    <Wrap borderRadius="lg" bg="primary.500"
                        gap={6}
                        p="30px"
                        width="90%">


                        {
                        this.state.highestrated.map((movie, index) => (
                            <Box w="15rem" bg="primary.500" p="5px" borderRadius="md" onClick={() => this.handleClick(movie)}>

                                <div  key={
                                    movie.titleId
                                }>

                                    <Center> 
                                    <img src={
                                            movie.cover_url
                                        }
                                        style={
                                            {

                                                width: "50%",
                                                height: "auto",
                                                borderRadius:"5%"

                                            }

                                        }/>
                                    </Center> 
                                    <Text color="white" >{
                                        movie.primaryTitle
                                    }</Text>

                                    <p style={{color:"white"}}>Rated {
                                        movie.avgRating
                                    }
                                        by {
                                        movie.numOfVotes
                                    }
                                        viewers.</p>
                                </div>

                            </Box>
                        ))
                    } </Wrap>

    </Center>
                </div>
            );
        }




}

export default HighestRatedMovies;
