import React, { Component, useState } from 'react'
import {Button, ButtonGroup, Input, Grid, GridItem, Box,Stack, SimpleGrid,  Radio, RadioGroup} from "@chakra-ui/react"
import axios from 'axios'
import Suggestions from '../Components/Suggestions'



const API_URL = 'https://teampolaris.web.illinois.edu/search-movies'

class AdvancedSearch extends Component {
  state = {
    query: '',
    results: [],
    queryTitleId: '',
    movieName:'',
    movieGenre:'',
    movieRegion:'',
    movieRating:''

  }

  getInfo = () => {
    axios.get(`${API_URL}/${this.state.query}`)
      .then(({ data }) => {
        this.setState({
          results: data
        })
      })
  }

  handleInputChange = () => {
    this.setState({
      query: this.search.value
    }, () => {
      if (this.state.query && this.state.query.length > 1) {
        if (this.state.query.length % 2 === 0) {
          this.getInfo()
        }
      } else if (!this.state.query) {
      }
    })
  }

  onSubmitHandler = (e) => {
    e.preventDefault();
    this.state.results.map(r => {
        if (r.primaryTitle == this.state.query){
          this.state.queryTitleId= r.titleId
      };
    });
    console.log(this.state.queryTitleId); 


  }


  render() {
    const [value, setValue] = useState("1")
    return (


      <form onSubmit={this.onSubmitHandler}>

        <Input list="browsers" name="browser" id="browser"
            style={
                {
                    marginTop: "20px",
                    width: "75%",
                    height:"50px",
                    borderRadius: "40px",
                    marginRight:"20px",
                    marginBottom:"20px"
                }
            }
            focusBorderColor="orange.400"
            size="lg"
            bg="white"
            placeholder="Search"
            ref={input => this.search = input}
            onChange={this.handleInputChange}
        />

        <Suggestions results={this.state.results} />

        <Button colorScheme="pink" variant="solid" type="submit" onClick={this.onSubmitHandler}>Submit</Button>


        <Grid bg="orange.500" borderRadius="lg" padding="10px" margin="10px" >
                <GridItem overflowY="scroll" maxHeight="40vh">
                <h2>Genre</h2>
                <RadioGroup onChange={setValue} value={value}>
                        <Stack direction="column" >
                        <Radio value="1">Drama</Radio>
                        <Radio value="2">Action</Radio>
                        <Radio value="3">Crime</Radio>
                        <Radio value="4">Comedy</Radio>
                        <Radio value="5">Musical</Radio>
                        <Radio value="6">Fantasy</Radio>
                        <Radio value="7">Romance</Radio>
                        <Radio value="8">Adventure</Radio>
                        <Radio value="9">Documentary</Radio>
                        <Radio value="10">Horror</Radio>
                        <Radio value="11">Sci-Fi</Radio>
                        <Radio value="12">Mystery</Radio>
                        <Radio value="13">Thriller</Radio>
                        <Radio value="14">Biography</Radio>
                        <Radio value="15">Music</Radio>
                        <Radio value="16">Animation</Radio>
                        <Radio value="17">Family</Radio>
                        <Radio value="18">War</Radio>
                        <Radio value="19">History</Radio>
                        <Radio value="20">Adult</Radio>
                        <Radio value="21">Sport</Radio>
                        <Radio value="22">Western  </Radio>                     
                        </Stack>
                </RadioGroup>
                </GridItem>
                </Grid>

      </form>


    )

  }
}

export default AdvancedSearch
