import {
    Button, 
    Input,
    Wrap,
    Text,
    Flex,
    WrapItem,
    Textarea
} from "@chakra-ui/react"



export default function ReviewPage() {

    return (
        <div>



               <Wrap gridTemplateColumns="auto 1fr" backgroundColor="primary.100" height="55vw">
                   <WrapItem irection="column">
                       <h1 style={{color:"white", paddingLeft:"20px"}}>Name of the movie </h1>
                       <Textarea placeholder="Here is a sample placeholder" ></Textarea>
                       <Button style={{padding:"20px"}}>Add Review</Button>
                   </WrapItem>
               </Wrap>


        </div>
    );
}
