import React, {Component} from 'react';
import {
    Grid,
    GridItem,
    Box,
    Text,
    Center
} from "@chakra-ui/react";
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
// Import Swiper styles
import 'swiper/swiper.scss';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';


class TrendingMovies extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            trending: []
        };
    }


    getTrending() {
        fetch('http://18.206.168.148:5000/movies/trending ').then(response => response.json()).then(response => {
            this.setState({trending: response})
            console.log(this.state)
        })
    }


    componentDidMount() {
        this.getTrending();
    }


    render() {


        return (

            <div>


                <Grid borderRadius="lg" bg="primary.500" templateColumns="repeat(4, 1fr)"
                    gap={6}
                    p="30px"
                    width="100%"
                    >
<div>test</div>
<Swiper
    width="50%"
      spaceBetween={5}
      slidesPerView={2}
      navigation
      pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log('slide change')}
    >
                    {

                    this.state.trending.map((movie, index) => (
                        <SwiperSlide>
                        <Box bg="white" p="5px" borderRadius="md">
                            <div key={
                                movie.titleId
                            }>
                                <img style={{display: "inline"}}
                                src={
                                        movie.cover_url
                                    }
                                    width="200"
                                    height="150"

                                    />
                                <h1>{
                                    movie.primaryTitle
                                }</h1>
                                <p>Rated {
                                    movie.avgRating
                                }
                                    by {
                                    movie.numOfVotes
                                }
                                    viewers.</p>
                            </div>
                        </Box>
                        </SwiperSlide>
                    ))

                }   </Swiper>
                </Grid>


            </div>
        );
    }


}

export default TrendingMovies;
