import React, { Component } from 'react';
import {Grid, GridItem, Input, Stack} from "@chakra-ui/react";

class TrendingMovies extends React.Component{
    constructor(props) {
        super(props);
        this.state = { 
          trending:[] 
        };
      }


      getTrending() {
        fetch('http://18.206.168.148:5000/movies/trending ')
          .then(response => response.json())
          .then(response => {
            this.setState({
              trending: response
            })
            console.log(this.state)
          })
      }


      componentDidMount() {
        this.getTrending(); 
      }

      render(){
          return (
        <div>
        <Grid colSpan={4} bg="yellow"> 

        {this.state.trending.map((movie, index) => (
        <div key={movie.titleId}>
          <h1>{movie.primaryTitle}!</h1>
          <p>Rated {movie.avgRating} by {movie.numOfVotes} viewers.</p>
        </div>
    ))}

        </Grid>
        </div>
          );
      }



}

export default TrendingMovies;
