import React from 'react'
import {
    Grid,
    GridItem,
    Input,
    Stack,
    Center,
    Text,
    Flex, Avatar
} from "@chakra-ui/react"

const ActorSuggestions = (props) => {

    const optionsactor = props.results.map(r => (
        <select style="display: block;">
      <option  key ={r._id} id={r._id}  value={r._name}/>
      </select>
     ))
    return <datalist  style={{display:"block"}} id="actors"><Text color="white" >{optionsactor}</Text></datalist>

}

export default ActorSuggestions
